const moduleB = {
    state: { //唯一数据源，我理解为声明全局变量
        users:[
            {id:'115757',password:'123456',headImg:'./images/headImg.jpg',lend:[{bookId:'1234/2342',startTime:new Date("2019-09-07 10:50:20"),day:3},{bookId:'1234/2342',startTime:new Date("2019-09-08 10:50:20"),day:5}]}
        ],
        login:{loginStatus:'未登录',loginUser:[]},
        registeredStatus:''
    },
    getters: {//针对state数据的过滤，
        loginMsg:state => {
            return state.login
        },
        registeredStatusMsg:state => {
            return state.registeredStatus
        }
    },
    mutations: { //逻辑处理，但Mutation 必须是同步函数
        login : (state,user) => {
            state.users.forEach(item => {
                if (item.id == user.id){
                    if (item.password == user.password){
                        state.login.loginStatus = "成功"
                        state.login.loginUser = item
                    }
                    else{
                        state.login.loginStatus =  "密码错误"
                    }
                }
                else{
                    state.login.loginStatus =  "账号不存在"
                }
            })
        },
        registeredStatus:(state,user) => {
            state.users.forEach(item => {
                if (item.id == user.id){
                    state.registeredStatus = '账号已存在'
                }
                else{
                    let msg = {id:user.id,password:user.password,headImg:'./images/headImg.jpg',lend:[]}
                    state.users.push(msg)
                    state.registeredStatus = '注册成功'
                    console.log(state.users)
                }
            })
        },
        esc:state => {
            state.login = {loginStatus:'未登录',loginUser:[]}
        },

        lendBook:(state,bookMsg) => {
            state.login.loginUser.lend.push(bookMsg)
        }

    },
    actions: {//Action 类似于 mutation,Action 提交的是 mutation，而不是直接变更状态;Action 可以包含任意异步操作.

     }
}

const store = new Vuex.Store({
    modules: {
        a: moduleA,
        b: moduleB
    }
})

export default store

store.state.a // -> moduleA 的状态
store.state.b // -> moduleB 的状态
